$(document).ready(function(){var e=$("#hiddenContent");$("#showHideContent").click(function(t){t.preventDefault();if(e.is(":hidden")){e.slideDown("fast");$(this).html("Компактный поиск")}else{e.slideUp("fast");$(this).html("Расширенный поиск")}});var t=$(".overlay");var n=$(".modal");$(".call-button").click(function(e){e.preventDefault();t.fadeIn(200);n.fadeIn(200)});$(".modal__close").click(function(e){e.preventDefault();$(".overlay, .modal").hide()});t.click(function(){$(this).hide();n.hide()});$(".catalog__sort a").click(function(e){e.preventDefault();if($(this).hasClass("active")){if($(this).find(".catalog__icon").hasClass("catalog__icon_type_down")){$(this).find(".catalog__icon").removeClass("catalog__icon_type_down");$(this).find(".catalog__icon").addClass("catalog__icon_type_up")}else{$(this).find(".catalog__icon").removeClass("catalog__icon_type_up");$(this).find(".catalog__icon").addClass("catalog__icon_type_down")}}else{$(".catalog__sort a").removeClass("active");$(this).addClass("active");$(this).find(".catalog__icon").show()}})})